import axios from "axios";
import { useEffect, useState } from "react";
import Header from "./Header";


const ShowBook = () => {
    const [books, setBooks] = useState([])
    const [search, setSearch] = useState("the+lost+world")
    const [result, setResult] = useState("")
    const [loading, setLoading] = useState(true)
    const baseURL = 'http://openlibrary.org/search.json?title='

    const getBook = () => {
        setLoading(true)
        axios.get(`${baseURL}${search}`)
            .then(response => {
                console.log(response)
                const {list} = response.data.docs

                if(list){
                    const newBooks = list.slice(0, 20)
                    .map((book) => {
                        const {key, author_name, cover_i, edition_count, first_publish_year, title} = book;
        
                        return {
                            id: key,
                            author: author_name,
                            cover_id: cover_i,
                            edition_count: edition_count,
                            year: first_publish_year,
                            title: title
                        }
                    })
                    
                    setBooks(newBooks);

                    if(newBooks.length > 1){
                        setResult("Your Search Result")
                    }else {
                        setResult("No Search Result Found!")
                    }
                } else {
                    setBooks([])
                    setResult("No Search Result Found!")
                }
                setLoading(false)
            })
            .catch(error => {
                console.log(error);
                setLoading(false);
            });
    }

    useEffect(() => {
        getBook()
    }, [])

}

export default ShowBook;
