import axios from "axios";
import { useEffect, useState, createContext, useCallback } from "react";
import Header from "./Header";

export const DataContext = createContext();
const Data = ({children}) => {
    const [books, setBooks] = useState([])
    const [search, setSearch] = useState("the+lost+world")
    const [result, setResult] = useState("")
    const [loading, setLoading] = useState(true)
    const baseURL = 'http://openlibrary.org/search.json?title='

    const bookDatas = useCallback(async() => {
        setLoading(true);
        try{
            const response = await fetch(`${baseURL}${search}`)
            const datas = await response.json()
            console.log(datas)
            const {list} = datas
        } catch(error) {
            console.log(error)
        }
    })

    const getBook = async () => {
        setLoading(true)
        axios.get(`${baseURL}${search}`)
            .then(response => {
                console.log(response)
                const {list} =  JSON.stringify(response.docs)
                console.log(list)

                if(list){
                    const newBooks = list.slice(0, 20)
                    .map((book) => {
                        const {key, author_name, cover_i, edition_count, first_publish_year, title} = book;
        
                        return {
                            id: key,
                            author: author_name,
                            cover_id: cover_i,
                            edition_count: edition_count,
                            year: first_publish_year,
                            title: title
                        }
                    })
                    
                    setBooks(newBooks);

                    if(newBooks.length > 1){
                        setResult("Your Search Result")
                    }else {
                        setResult("No Search Result Found!")
                    }
                } else {
                    setBooks([])
                    setResult("No Search Result Found!")
                }
                setLoading(false)
            })
            .catch(error => {
                console.log(error);
                setLoading(false);
            });
    }

    useEffect(() => {
        getBook()
    }, [])

    return(
        <DataContext.Provider value={{books, setSearch, loading, result, setResult}}>
            {children}
        </DataContext.Provider>
    )
}

export default Data;
