import axios from "axios";
import { useEffect, useState } from "react";
import Header from "./Header";


const ShowBook = () => {
    const [book, setBook] = useState([])
    const [loading, setLoading] = useState(true)

    const getBook = () => {
        axios.get('http://openlibrary.org/search.json?title=the+lost+world')
            .then(response => {
                console.log(response);
                setBook(response.data)
            })
            .catch(error => {
                console.log(error);
            });
    }

    useEffect(() => {
        getBook()
    }, [])

    return (
        <div>
            {<Header />}
            <div className="mt-[100px] font-bold text-[40px] text-center">
                <h1>Find Your Book</h1>
                <div class="flex items-center justify-center">
                    <label for="simple-search" class="sr-only">Search</label>
                    <div class="relative -ml-[100px]">
                        <div class="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                            <svg aria-hidden="true" class="w-5 h-5 dark:text-white" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clip-rule="evenodd"></path></svg>
                        </div>
                        <input type="text" id="simple-search" class="bg-[#BBC3CF] placeholder:text-white border border-gray-300 text-white text-md rounded-lg focus:ring-blue-500 focus:border-blue-500 block pl-10 p-2.5" />
                    </div>
                    <button type="submit" class="p-2.5 ml-2 text-sm font-medium text-white bg-[#22C55E] rounded-lg outline-none border border-green-700 hover:bg-green-600 focus:ring-2 focus:outline-none focus:ring-green-400">
                        Search
                        <span class="sr-only">Search</span>
                    </button>
                </div>
            </div>
            {book.title}
        </div>
    );
}

export default ShowBook;
