            .then(response => {
                console.log(response);
                setBooks(response.data)
            })
            .catch(error => {
                console.log(error);
                setLoading(false);
            });
    }

    useEffect(() => {
        getBook()
    }, [])

    return (
        <div>
            {<Header />}
            <div className="mt-[100px] font-bold">
                <h1 className="vsm:text-[40px] text-[30px] text-center">Find Your Book</h1>
                <div className="flex items-center mt-[20px] justify-center">
                    <div className="relative gap-5">
                        <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                            <svg aria-hidden="true" className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clip-rule="evenodd"></path></svg>
                        </div>
                        <input 
                            type="text" 
                            id="" 
                            className="bg-[#dbe0e7] py-2 px-[10px] vsm:px-[50px] placeholder:text-gray-400 border border-gray-300 text-gray-700 rounded-lg outline-none focus:ring-2 focus:ring-slate-500" 
                            placeholder="Find your book title"
                            />
                    </div>
                    <button type="submit" className="p-2.5 ml-3 text-sm font-medium text-white bg-[#22C55E] rounded-lg hover:bg-green-600  focus:outline-none">
                        Search
                    </button>
