import { useParams } from "react-router-dom";
import Loader from "../Loader/Loader";
import notFound from '../../image/cover_not_found.jpg'
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Navbar from "../Main/Navbar";

const baseURL = "https://openlibrary.org/works/"

const BookDetails = () => {
    const {id} = useParams()
    const [loading, setLoading] = useState(false)
    const [book, setBook] = useState(null)
    const navigate = useNavigate()

    useEffect(() => {
        setLoading(true)
        async function getBookDetails(){
            try{
                const response = await fetch(`${baseURL}${id}.json`)
                const data = await response.json()

                if(data){
                    const {description, title, covers, subject_places, subject_times, subject} = data
                    const detail = {
                        description: description ? description.value : "No Description Found!",
                        title: title,
                        cover_img: covers ? `https://covers.openlibrary.org/b/id/${covers[0]}-L.jpg` : notFound,
                        subject_places: subject_places ? subject_places.join(", ") : "No Subject Places Found!",
                        subject_times: subject_times ? subject_times.join(", ") : "No Subject Times Found!",
                        subject: subject ? subject.join(", ") : "No Subject Found!",
                    }
                    setBook(detail)
                }else{
                    setBook(null)
                }
                setLoading(false)

            } catch(error){
                console.log(error)
                setLoading(false)
            }
        }
        getBookDetails()
    }, [id])

    console.log(book)

    return (
        <div>
            <Navbar/>
            <div>
                
            </div>
        </div>
    );
}

export default BookDetails;
